import { useId } from 'react'
import clsx from 'clsx'

export function Logomark({
  invert = false,
  filled = false,
  ...props
}: React.ComponentPropsWithoutRef<'svg'> & {
  invert?: boolean
  filled?: boolean
}) {
  const id = useId()

  return (
    <svg viewBox='0 0 30 31' aria-hidden='true' {...props}>
      <rect
        clipPath={`url(#${id}-clip)`}
        className={clsx(
          'h-8 transition-all duration-300',
          invert ? 'fill-white' : 'fill-slate-950',
          filled ? 'w-8' : 'w-0 group-hover/logo:w-8',
        )}
      />
      <use
        href={`#${id}-path`}
        className={invert ? 'stroke-white' : 'stroke-slate-950'}
        fill='none'
        strokeWidth='1'
      />
      <defs>
        <path
          id={`${id}-path`}
          d='M1 30V1H13.9625C16.9721 1 19.4323 1.49698 21.3429 2.49093C23.2661 3.47346 24.6897 4.80444 25.6138 6.48387C26.5379 8.16331 27 10.037 27 12.1048C27 14.1727 26.5379 16.0521 25.6138 17.7429C24.7022 19.4338 23.2911 20.7819 21.3804 21.7873C19.4697 22.7813 17.0221 23.2782 14.0375 23.2782L6 23.2679C6 24 7 22.2425 7 30H1Z'
        />
        <clipPath id={`${id}-clip`}>
          <use href={`#${id}-path`} />
        </clipPath>
      </defs>
    </svg>
  )
}

